  pk_GateId,
  gtSe_GateName,
  gtSe_GateIp,
  gtSe_GatePort,
  gtSe_CreateTime,
  gtSe_UpdateTime,
 gtSe_GateState
from tbl_gateservice


     pk_GateId=#{pkGateid}


     and gtSe_GateName=#{gtseGatename}


     and gtSe_GateIp=#{gtseGateip}


     and gtSe_GatePort=#{gtseGateport}


     and gtSe_CreateTime=#{gtseCreatetime}


     and gtSe_UpdateTime=#{gtseUpdatetime}

