mapper.update:
update p_m_menu
menu_id=#{menuId}, menu_type=#{menuType}, contents=#{contents}, sort_number=#{sortNumber}, url=#{url}, rel=#{rel}, parent_menu_id=#{parentMenuId}, notes=#{notes}, last_update_date=sysdate(), last_update_user=#{lastUpdateUser},
menu_id=#{menuId}

mapper.select:
select t.menu_id, t.menu_type, t.contents, t.sort_number, t.url, t.rel, t.parent_menu_id, m.contents parentMenuName, t.notes, t.create_date, t.create_user, t.last_update_date, t.last_update_user from p_m_menu t left join p_m_menu m on t.parent_menu_id=m.menu_id
and t.menu_id=#{menuId} and t.menu_type=#{menuType} and t.contents like concat('%',#{contents},'%') and t.sort_number=#{sortNumber} and t.url like concat('%',#{url},'%') and t.rel=#{rel} and t.parent_menu_id=#{parentMenuId} and t.notes=#{notes}
order by t.PARENT_MENU_ID ASC, t.sort_number asc,t.menu_id asc limit #{pager.offset},#{pager.numPerPage}

select
count(1)
from
p_m_menu t

	 and t.menu_id=#{menuId}
	 and t.menu_type=#{menuType}
	 and t.contents like concat('%',#{contents},'%')
	 and t.sort_number=#{sortNumber}
	 and t.url like concat('%',#{url},'%')
	 and t.rel=#{rel}
	 and t.parent_menu_id=#{parentMenuId}
	 and t.notes=#{notes}
