SELECT pk_Bespoke, besp_ResePaymentCode, (ep.elPi_ElectricPileCode) AS eleCode, (ui.norm_real_name) AS username, (go.grou_GroupName) AS groupname, besp_BespokePrice, besp_BeginTime, besp_EndTime, besp_RealityTime, (eh.ePHe_ElectricpileHeadName) AS eleHeadName, besp_BespokeStatus, besp_OrderType, ep.elPi_UserName as comName FROM tbl_Bespoke bp INNER JOIN tbl_User_normal AS ui ON ui.user_id = bp.besp_UserInfo INNER JOIN tbl_Group AS go ON go.pk_Group = ui.usIn_GroupID INNER JOIN tbl_ElectricPile AS ep ON ep.pk_ElectricPile = bp.besp_ElectricPileId INNER JOIN tbl_ElectricPileHead AS eh ON eh.pk_ElectricpileHead = ep.pk_ElectricPile
and besp_ResePaymentCode=#{bespResepaymentcode}
and besp_BespokeStatus=#{bespBespokestatus}
and ui.norm_real_name like concat('%',#{username},'%')
order by besp_BeginTime desc

SELECT pk_Bespoke, besp_ResePaymentCode, (ep.elPi_ElectricPileCode) AS eleCode, ep.elPi_ElectricPileAddress, bp.besp_BespokePrice, besp_FrozenAmt, ep.owner_ship, pu.puhi_InvoiceStatus, ui.user_account AS userPhone, ui.norm_real_name AS username, besp_BespokePrice, besp_BeginTime, besp_EndTime, besp_RealityTime, ( eh.ePHe_ElectricpileHeadName ) AS eleHeadName, ep.elPi_ElectricPileName extValue1, besp_BespokeStatus, besp_FrozenAmt, besp_OrderType FROM tbl_Bespoke bp INNER JOIN ( SELECT a.user_account, b.user_id, b.norm_real_name FROM tbl_user a, tbl_user_normal b WHERE a.user_leval = '6' AND a.user_id = b.user_id ) AS ui ON ui.user_id = bp.besp_UserInfo LEFT JOIN tbl_ElectricPile AS ep ON ep.pk_ElectricPile = bp.besp_ElectricPileId LEFT JOIN tbl_ElectricPileHead AS eh ON eh.pk_ElectricpileHead = bp.besp_ElectricPileHead LEFT JOIN tbl_purchasehistory AS pu ON pu.puHi_BespokeNumber = bp.besp_ResePaymentCode
(besp_ResePaymentCode like concat('%',#{bespResepaymentcode},'%') or ep.elPi_ElectricPileName like concat('%',#{bespResepaymentcode},'%') or ep.elPi_ElectricPileCode like concat('%',#{bespResepaymentcode},'%') or ui.user_account like concat('%',#{bespResepaymentcode},'%') )
and besp_BespokeStatus=#{bespBespokestatus}
and besp_OrderType=#{bespOrderType}
and ui.norm_real_name like concat('%',#{username},'%')
and ep.elPi_UserName = #{bespUser}
and besp_BeginTime>=#{startDate}
and besp_EndTime<= date_add(#{endDate}, interval 1 day)
and ep.elPi_OwnProvinceCode=#{provinceCode}
and ep.elPi_OwnCityCode=#{cityCode}
and ep.elPi_OwnCityCode=#{areaCode}
and pu.puhi_InvoiceStatus= #{puhiInvoiceStatus}
order by besp_BeginTime desc limit #{pager.offset},#{pager.numPerPage}

select
count(pk_Bespoke)	
FROM tbl_Bespoke bp inner JOIN (select a.user_account,b.user_id,b.norm_real_name from tbl_user a,tbl_user_normal b where a.user_leval='6' and a.user_id=b.user_id) AS ui ON ui.user_id = bp.besp_UserInfo
		    left JOIN tbl_ElectricPile AS ep ON ep.pk_ElectricPile = bp.besp_ElectricPileId
		    left JOIN tbl_ElectricPileHead AS eh ON eh.pk_ElectricpileHead = bp.besp_ElectricPileHead
			left JOIN tbl_purchasehistory AS pu ON pu.puHi_BespokeNumber = bp.besp_ResePaymentCode


		(besp_ResePaymentCode  like concat('%',#{bespResepaymentcode},'%')
		or ep.elPi_ElectricPileName like concat('%',#{bespResepaymentcode},'%')
		or ep.elPi_ElectricPileCode like concat('%',#{bespResepaymentcode},'%')
		or ui.user_account like concat('%',#{bespResepaymentcode},'%')
		)


		and besp_BespokeStatus=#{bespBespokestatus}


		and besp_OrderType=#{bespOrderType}


		and ui.norm_real_name like concat('%',#{username},'%')


		and ep.elPi_UserName = #{bespUser}


		and besp_BeginTime>=#{startDate}


		and besp_EndTime<= date_add(#{endDate}, interval 1 day)



		and ep.elPi_OwnProvinceCode=#{provinceCode}


		and ep.elPi_OwnCityCode=#{cityCode}


		and ep.elPi_OwnCityCode=#{areaCode}


		and pu.puhi_InvoiceStatus= #{puhiInvoiceStatus}

SELECT pk_Bespoke, besp_ResePaymentCode, (ep.elPi_ElectricPileCode) as eleCode, (ui.norm_real_name) as username, (ui.user_account) as usPhone, besp_BespokePrice, besp_BeginTime, besp_EndTime, (eh.ePHe_ElectricpileHeadName) as eleHeadName, besp_Bespokestatus, besp_OrderType, besp_RealityTime FROM tbl_Bespoke bp INNER JOIN (SELECT a.user_account, b.* FROM tbl_user a, tbl_user_normal b WHERE a.user_id = b.user_id) AS ui ON ui.user_id = bp.besp_UserInfo INNER JOIN tbl_ElectricPile AS ep ON ep.pk_ElectricPile = bp.besp_ElectricPileId INNER JOIN tbl_ElectricPileHead AS eh ON eh.pk_ElectricpileHead = ep.pk_ElectricPile
and besp_ResePaymentCode=#{bespResepaymentcode}
and besp_Bespokestatus=#{bespBespokestatus}
and ui.norm_real_name like concat('%',#{username},'%')
and ui.user_account=#{usPhone}

SELECT pk_Bespoke, besp_ResePaymentCode, (ep.elPi_ElectricPileCode) as eleCode, ep.elPi_ElectricPileName chargePointName, ep.elPi_ElectricPileAddress chargePointAddress, (ui.norm_real_name) as username, (ui.norm_real_name) AS comName, (ui.user_account) as usPhone, besp_FrozenAmt as besp_BespokePrice, besp_BeginTime, besp_EndTime, (eh.ePHe_ElectricpileHeadName) as eleHeadName, besp_Bespokestatus, besp_OrderType, besp_RealityTime FROM tbl_Bespoke bp left JOIN tbl_ElectricPile AS ep ON ep.pk_ElectricPile = bp.besp_ElectricPileId inner join (select a.user_account, a.user_id,a.norm_real_name FROM tbl_user_normal_view a WHERE a.user_leval = '5') AS ui on ui.user_id = ep.elPi_UserId left JOIN tbl_ElectricPileHead AS eh ON eh.pk_ElectricpileHead = bp.besp_ElectricPileHead
1=1
and ep.elPi_UserId = #{bespUser}
and besp_ResePaymentCode like concat('%',#{bespResepaymentcode},'%')
and besp_Bespokestatus=#{bespBespokestatus}
and besp_OrderType=#{bespOrderType}
and ui.norm_real_name like concat('%',#{username},'%')
and ui.user_account=#{usPhone}
and ep.elPi_ElectricPileName like concat('%',#{chargePointName},'%')
and ep.elPi_ElectricPileAddress like concat('%',#{chargePointAddress},'%')
and besp_BeginTime>=#{bespBeginTimes}
and besp_EndTime<=date_add(#{bespEndTimes}, interval 1 day)
and ep.elPi_ElectricPileCode like concat('%',#{eleCode},'%')
order by besp_BeginTime desc limit #{pager.offset},#{pager.numPerPage}

select count(pk_Bespoke) from tbl_Bespoke bp LEFT JOIN tbl_ElectricPile AS ep ON ep.pk_ElectricPile = bp.besp_ElectricPileId inner join (select a.user_account, a.user_id,a.norm_real_name FROM tbl_user_normal_view a WHERE a.user_leval = '5') AS ui on ui.user_id = ep.elPi_UserId LEFT JOIN tbl_ElectricPileHead AS eh ON eh.pk_ElectricpileHead = bp.besp_ElectricPileHead
1=1
and ep.elPi_UserId = #{bespUser}
and besp_ResePaymentCode like concat('%',#{bespResepaymentcode},'%')
and besp_Bespokestatus=#{bespBespokestatus}
and besp_OrderType=#{bespOrderType}
and ui.norm_real_name like concat('%',#{username},'%')
and ui.user_account=#{usPhone}
and ep.elPi_ElectricPileName like concat('%',#{chargePointName},'%')
and ep.elPi_ElectricPileAddress like concat('%',#{chargePointAddress},'%')
and besp_BeginTime>=#{bespBeginTimes}
and besp_EndTime<=date_add(#{bespEndTimes}, interval 1 day)
and ep.elPi_ElectricPileCode like concat('%',#{eleCode},'%')

SELECT pk_Bespoke, besp_ResePaymentCode, (ep.elPi_ElectricPileCode) AS eleCode, (pb.puBu_CompanyName) AS comName, besp_BespokePrice, besp_BeginTime, besp_EndTime, (eh.ePHe_ElectricpileHeadName) AS eleHeadName, besp_Bespokestatus, besp_OrderType, besp_RealityTime FROM tbl_Bespoke bp INNER JOIN tbl_PureBusiness AS pb ON pb.pk_PureBusiness = bp.besp_UserInfo INNER JOIN tbl_Group AS go ON go.pk_Group = pb.puBu_GroupID INNER JOIN tbl_ElectricPile AS ep ON ep.pk_ElectricPile = bp.besp_ElectricPileId INNER JOIN tbl_ElectricPileHead AS eh ON eh.pk_ElectricpileHead = bp.besp_ElectricPileHead
and besp_ResePaymentCode=#{bespResepaymentcode}
and besp_Bespokestatus=#{bespBespokestatus}
and pb.puBu_CompanyName like concat('%',#{comName},'%')
and ep.elPi_ElectricPileCode like concat('%',#{eleCode},'%')
