mapper.select:
SELECT cast(DATE_FORMAT(createdate,'%Y%m') as signed
integer) AS 'month',COUNT(*) AS 'count'
FROM tbl_user_normal_view   a 
where 1=1

	and date_format(a.createdate,'%Y-%m-%d') >= #{begin_date}


	 and date_format(a.createdate,'%Y-%m-%d') <= #{end_date} 

GROUP BY cast(DATE_FORMAT(createdate,'%Y%m') as signed integer);

SELECT cast(DATE_FORMAT(createdate,'%Y%m') as signed integer) AS 'month', (case when a.norm_registe_type='1' then '管理后台' when a.norm_registe_type='2' then 'Web' when a.norm_registe_type='3' then 'android' when a.norm_registe_type='4' then 'ios' else '其他' end) as zcfrom, count(*) as count FROM tbl_user_normal_view a where 1=1
and date_format(a.createdate,'%Y-%m-%d') >= #{begin_date}
and date_format(a.createdate,'%Y-%m-%d') <= #{end_date}
GROUP BY cast(DATE_FORMAT(createdate,'%Y%m') as signed integer),a.norm_registe_type;

select cast(DATE_FORMAT(puHi_Createdate,'%Y%m') as signed integer) AS 'month',
(case when puHi_Type='1' then '充电消费'  when puHi_Type='2' then '预约消费'  when puHi_Type='3' then '购物消费' when puHi_Type='4' then '充值' else '其他' end) as 'consumptionType',
count(*) as 'count',sum(puHi_Monetary) as 'amount'
from tbl_purchasehistory 
WHERE 1=1


	and date_format(puHi_Createdate,'%Y-%m-%d') >= #{begin_date}



	and date_format(puHi_Createdate,'%Y-%m-%d') <= #{end_date} 

group by cast(DATE_FORMAT(puHi_Createdate,'%Y%m') as signed integer),puHi_Type;

select cast(DATE_FORMAT(puHi_Createdate,'%Y%m') as signed integer) AS 'month',
(case when puHi_ChargeType='1' then '支付宝充值'  when puHi_ChargeType='2' then '微信充值'  when puHi_ChargeType='3' then '银联充值' else '平台人工充值' end) as 'puHiChargeType',
count(*) as 'count',sum(puHi_Monetary) as 'amount'
from tbl_purchasehistory 
WHERE puHi_Type = '4'


	and date_format(puHi_Createdate,'%Y-%m-%d') >= #{begin_date}



	and date_format(puHi_Createdate,'%Y-%m-%d') <= #{end_date} 

group by cast(DATE_FORMAT(puHi_Createdate,'%Y%m') as signed integer),puHi_ChargeType;

select substring(b.city_name, 1, 3) as '城市', (case when a.elPi_State='10' then '专属' when a.elPi_State='15' then '分享' end) as '桩体类型', count(*) as '数量' from tbl_electricpile a ,tb_m_city b,tbl_user c where a.elPi_OwnCityCode = b.city_id and a.elPi_UserId = c.user_id and a.elPi_State in ('10','15')
and c.user_id = #{value}
group by a.elPi_OwnCityCode,a.elPi_State order by a.elPi_State,a.elPi_OwnCityCode;

select substring(b.city_name, 1, 3) as '所在城市',count(*) as '上线数量',(case when a.elPi_ChargingMode='5' then '快充' when a.elPi_ChargingMode='14' then '慢充' else '其他' end) as '充电类型',
	(case when a.elPi_PowerUser = '3' then '电动车' when a.elPi_PowerUser='4' then '电动自行车' when a.elPi_PowerUser='13' then '多功能' else '其他' end) as '电桩类型'
	from tbl_electricpile a ,tb_m_city b,tbl_user c where  a.elPi_OwnCityCode = b.city_id and a.elPi_UserId = c.user_id

		and c.user_id = #{value}

	group by a.elPi_OwnCityCode,a.elPi_PowerUser,elPi_ChargingMode order by a.elPi_ChargingMode desc;

select   cast(DATE_FORMAT(chOr_Createdate,'%Y%m') as signed integer) AS '收益月份',sum(a.chOr_Moeny) as '充电收益',sum(a.chOr_QuantityElectricity) as '总电量',count(*) as '充电次数'
from tbl_chargingorder a,
		tbl_electricpile b,
		tbl_user c  where a.chOr_PileNumber = b.elPi_ElectricPileCode
					  AND b.elPi_UserId = c.user_id
					  AND a.chOr_ChargingStatus >= '2'
					  and a.chOr_ChargingStatus!=4

	and c.user_id = #{userId}


	and date_format(a.chOr_Createdate,'%Y-%m-%d') >=#{startDate} 


	and date_format(a.chOr_Createdate,'%Y-%m-%d') < #{endDate} 

group by cast(DATE_FORMAT(chOr_Createdate,'%Y%m') as signed integer) order by chOr_Createdate;

SELECT c.CITY_NAME AS '城市',	count(*) AS '故障次数', e.fc_FaultCause AS '故障原因'
FROM tbl_chargingfaultrecord a,	tbl_faultcode e,
	 tbl_electricpile b,	tb_m_city c,tbl_user d
WHERE a.cFRe_FaultCause = e.fc_FaultID
and a.cFRe_ElectricPileID = b.pk_ElectricPile
AND b.elPi_OwnCityCode = c.CITY_ID
AND b.elPi_UserId = d.user_id

	and d.user_id = #{userId}


	and date_format(a.cFRe_Createdate,'%Y-%m-%d') >=#{startDate} 


	and date_format(a.cFRe_Createdate,'%Y-%m-%d') < #{endDate} 

group by b.elPi_OwnCityCode,a.cFRe_FaultCause order by a.cFRe_FaultCause,b.elPi_OwnCityCode;

select c.CITY_NAME as '城市',count(a.pk_ElectricPile) as '电桩数量' from tbl_electricpilehead a, tbl_electricpile b ,tb_m_city c,tbl_user d where a.pk_ElectricPile= b.pk_ElectricPile and b.elPi_OwnCityCode=c.CITY_ID AND b.elPi_UserId = d.user_id and a.ePHe_ElectricpileHeadState=3
and d.user_id = #{userId}
group by elPi_OwnCityCode order by elPi_OwnCityCode;

select c.CITY_NAME as '城市',count(a.pk_ElectricPile) as '电桩数量' from tbl_electricpilehead a, tbl_electricpile b ,tb_m_city c,tbl_user d where a.pk_ElectricPile= b.pk_ElectricPile and b.elPi_UserId = d.user_id and b.elPi_OwnCityCode=c.CITY_ID and a.ePHe_ElectricpileHeadState=6
and d.user_id = #{userId}
group by elPi_OwnCityCode order by elPi_OwnCityCode;

select a.PROVINCE_NAME province_name,b.pile_count pile_count from tb_m_province a LEFT JOIN (select t.elPi_OwnProvinceCode,count(1) pile_count from tbl_electricpile t,tbl_user p where t.elPi_UserId = p.user_id and t.elPi_State in ('10','15')
and p.user_id = #{userId}
group by t.elPi_OwnProvinceCode) b on a.PROVINCE_ID=b.elPi_OwnProvinceCode;

SELECT cast(DATE_FORMAT(besp_Createdate, '%Y%m') AS signed INTEGER) AS 'month', count(besp_ElectricPileId) AS 'orderNumber', sum(besp_BespokeTime) AS 'totalChargingTime' FROM tbl_bespoke a, tbl_electricpile b, tbl_user c WHERE a.besp_ElectricPileId = b.pk_ElectricPile AND b.elPi_UserId = c.user_id
and c.user_id = #{userId}
and date_format(besp_Createdate,'%Y-%m-%d') >= #{beginDate}
and date_format(besp_Createdate,'%Y-%m-%d') <= #{endDate}
GROUP BY cast(DATE_FORMAT(besp_Createdate, '%Y%m') AS signed INTEGER);

select cast(DATE_FORMAT(chOr_Createdate,'%Y%m') as signed integer) AS 'chargeMonth', sum(b.chOr_QuantityElectricity) as 'chargingDegree', count(1) as 'chargeNumber' from tbl_chargingorder b, tbl_electricpile c, tbl_user d where b.chOr_PileNumber = c.elPi_ElectricPileCode AND c.elPi_UserId = d.user_id and b.chOr_ChargingStatus!=4
and d.user_id = #{userId}
and date_format(chOr_Createdate,'%Y-%m-%d') >= #{beginDate}
and date_format(chOr_Createdate,'%Y-%m-%d') <= #{endDate}
group by cast(DATE_FORMAT(chOr_Createdate,'%Y%m') as signed integer)
