mapper.update:
update tbl_PowerStation set poSt_Name=#{postName},
poSt_Address=#{postAddress}, poSt_Longitude=#{postLongitude}, poSt_Latitude=#{postLatitude}, poSt_Phone=#{postPhone}, poSt_Eleids=#{postEleids}, poSt_Status=#{postStatus},
poSt_Createdate=#{postCreatedate}, poSt_Updatedate=#{postUpdatedate}, poSt_Remark=#{postRemark}, poSt_PowerUser=#{postPoweruser}, poSt_IsAppoint=#{postIsappoint} where pk_PowerStation=#{pkPowerstation}

mapper.select:
select pk_PowerStation, poSt_Name,
poSt_Address, poSt_Longitude, poSt_Latitude, poSt_Phone, poSt_Eleids, poSt_Status,
poSt_Createdate, poSt_Updatedate, poSt_Remark, poSt_PowerUser, poSt_IsAppoint from tbl_PowerStation where pk_PowerStation=#{pkPowerstation}

select pk_PowerStation, poSt_Name,
poSt_Address, poSt_Longitude, poSt_Latitude, poSt_Phone, poSt_Eleids, poSt_Status,
poSt_Createdate, poSt_Updatedate, poSt_Remark, poSt_PowerUser, poSt_IsAppoint from tbl_PowerStation where pk_PowerStation=#{pkPowerstation}

select 
  pk_PowerStation,
  poSt_Name,


  poSt_Address,
  poSt_Longitude,
  poSt_Latitude,
  poSt_Phone,
  poSt_Eleids,
  poSt_Status,

  poSt_Createdate,
  poSt_Updatedate,
  poSt_Remark,
  poSt_PowerUser,
  poSt_IsAppoint
from tbl_PowerStation


     pk_PowerStation=#{pkPowerstation}


     and poSt_Name=#{postName}




     and poSt_Address=#{postAddress}


     and poSt_Longitude=#{postLongitude}


     and poSt_Latitude=#{postLatitude}


     and poSt_Phone=#{postPhone}


     and poSt_Eleids=#{postEleids}


     and poSt_Status=#{postStatus}



     and poSt_Createdate=#{postCreatedate}


     and poSt_Updatedate=#{postUpdatedate}


     and poSt_Remark=#{postRemark}


     and poSt_PowerUser=#{postPoweruser}


     and poSt_IsAppoint=#{postIsappoint}

select 
  pk_PowerStation,
  poSt_Name,


  poSt_Address,
  poSt_Longitude,
  poSt_Latitude,
  poSt_Phone,
  poSt_Eleids,
  poSt_Status,

  poSt_Createdate,
  poSt_Updatedate,
  poSt_Remark,
  poSt_PowerUser,
  poSt_IsAppoint
from tbl_PowerStation


     pk_PowerStation=#{pkPowerstation}


     and poSt_Name=#{postName}




     and poSt_Address=#{postAddress}


     and poSt_Longitude=#{postLongitude}


     and poSt_Latitude=#{postLatitude}


     and poSt_Phone=#{postPhone}


     and poSt_Eleids=#{postEleids}


     and poSt_Status=#{postStatus}




     and poSt_Createdate=#{postCreatedate}


     and poSt_Updatedate=#{postUpdatedate}


     and poSt_Remark=#{postRemark}


     and poSt_PowerUser=#{postPoweruser}


     and poSt_IsAppoint=#{postIsappoint}

select last.pk_PowerStation,last.poSt_Name,last.poSt_Address,last.electricPileCount,last.avgServiceCharge,last.avgCommentStart,last.poSt_PowerUser,last.poSt_Longitude,last.poSt_Latitude ,round(6378.138*2*asin(sqrt(pow(sin( (#{postLatitude}*pi()/180-last.poSt_Latitude*pi()/180)/2),2)+cos(#{postLatitude}*pi()/180)*cos(last.poSt_Latitude*pi()/180)* pow(sin( (#{postLongitude}*pi()/180-last.poSt_Longitude*pi()/180)/2),2)))*1000) distance from ( select pst.pk_PowerStation,pst.poSt_Name,pst.poSt_Address,count(epl2.pk_ElectricPile) electricPileCount,avg(epl2.raIn_ServiceCharge) avgServiceCharge ,avg(epl2.prCo_CommentStart) avgCommentStart,pst.poSt_PowerUser,pst.poSt_Longitude,pst.poSt_Latitude from (select * from tbl_PowerStation where poSt_Status!=0 and poSt_Status!=3 and poSt_Status!=5) pst LEFT JOIN ( select epl.elPi_RelevancePowerStation,rif.raIn_ServiceCharge,epl.pk_ElectricPile,pct2.prCo_CommentStart from tbl_ElectricPile epl left JOIN tbl_RateInformation rif on rif.pk_RateInformation = epl.elPi_RateInformationId left JOIN (select avg(prCo_CommentStart) prCo_CommentStart,prCo_ProductId from tbl_ProductComment pct where pct.prCo_Comment_type=1 GROUP BY prCo_ProductId) pct2 on epl.pk_ElectricPile=pct2.prCo_ProductId ) epl2 on pst.pk_PowerStation=epl2.elPi_RelevancePowerStation GROUP BY pst.pk_PowerStation ) last
1=1
and last.poSt_PowerUser=#{postPoweruser}
and round(6378.138*2*asin(sqrt(pow(sin( (#{postLatitude}*pi()/180-last.poSt_Latitude*pi()/180)/2),2)+cos(#{postLatitude}*pi()/180)*cos(last.poSt_Latitude*pi()/180)* pow(sin( (#{postLongitude}*pi()/180-last.poSt_Longitude*pi()/180)/2),2)))*1000) <=#{screenRadius}
and last.poSt_Name like concat('%',#{searchName},'%')
order by
last.avgServiceCharge asc
, last.avgCommentStart desc
1=1
and last.poSt_PowerUser=#{postPoweruser}
and round(6378.138*2*asin(sqrt(pow(sin( (#{postLatitude}*pi()/180-last.poSt_Latitude*pi()/180)/2),2)+cos(#{postLatitude}*pi()/180)*cos(last.poSt_Latitude*pi()/180)* pow(sin( (#{postLongitude}*pi()/180-last.poSt_Longitude*pi()/180)/2),2)))*1000) <=#{screenRadius}
and last.poSt_Name like concat('%',#{searchName},'%')

select * from (select last.pk_PowerStation,last.poSt_Name,last.poSt_Address,last.electricPileCount,last.avgServiceCharge,last.avgCommentStart,last.poSt_PowerUser,last.poSt_Longitude,last.poSt_Latitude, round(6378.138*2*asin(sqrt(pow(sin( (#{postLatitude}*pi()/180-last.poSt_Latitude*pi()/180)/2),2)+cos(#{postLatitude}*pi()/180)*cos(last.poSt_Latitude*pi()/180)* pow(sin( (#{postLongitude}*pi()/180-last.poSt_Longitude*pi()/180)/2),2)))*1000) distance from ( select pk_PowerStation,poSt_Name,poSt_Address,count(epl2.pk_ElectricPile) electricPileCount,avg(epl2.raIn_ServiceCharge) avgServiceCharge ,avg(epl2.prCo_CommentStart) avgCommentStart,poSt_PowerUser,poSt_Longitude,poSt_Latitude from (select * from tbl_PowerStation where poSt_Status!=0 and poSt_Status!=3 and poSt_Status!=5) pst RIGHT JOIN ( select epl.elPi_RelevancePowerStation,rif.raIn_ServiceCharge,epl.pk_ElectricPile,pct2.prCo_CommentStart from ( select elPi_RelevancePowerStation,pk_ElectricPile,elPi_RateInformationId from tbl_electricpile where elPi_Binding = 1 and elPi_State!= 0 and elPi_State!=3 and elPi_State!=5
and (elPi_PowerUser = #{screenType} or elPi_PowerUser=13)
and elPi_ChargingMode = #{chargingMode}
and elPi_State = #{postStatus}
and elPi_PowerInterface = #{elpiPowerinterface}
) epl left JOIN tbl_RateInformation rif on rif.pk_RateInformation = epl.elPi_RateInformationId left JOIN (select avg(pss_CommentStar) prCo_CommentStart,pss_PsId prCo_ProductId from tbl_powerstationstar pct where pct.pss_Utatus=0 GROUP BY pss_PsId) pct2 on epl.pk_ElectricPile=pct2.prCo_ProductId ) epl2 on pst.pk_PowerStation=epl2.elPi_RelevancePowerStation GROUP BY pst.pk_PowerStation ) last) a where distance < 10000
order by avgServiceCharge
order by avgCommentStart desc
order by distance

   select last.pk_PowerStation,last.poSt_Status,last.poSt_Longitude,last.poSt_Latitude,last.elPi_PowerUser 
   from
		(SELECT * from (select * from tbl_powerstation where poSt_Status!=3 and poSt_Status!=5 and poSt_Status!=0) a 
		LEFT JOIN 
		(select pk_ElectricPile,elPi_Binding,elPi_RelevancePowerStation,elPi_ChargingMode,elPi_ChargingModeName,elPi_PowerUser 
		from tbl_electricpile  where elPi_Binding=1 

			 and elPi_ChargingMode = #{chargingMode} 


			 and elPi_PowerInterface = #{elpiPowerinterface} 


        and (elPi_PowerUser=#{postPoweruser} or elPi_PowerUser=13) 

		) b on a.pk_PowerStation = b.elPi_RelevancePowerStation 
		where elPi_RelevancePowerStation is not null GROUP BY a.pk_PowerStation) last 
	where 1=1 

       and last.poSt_Status=#{postStatus}


        and last.poSt_IsAppoint=#{postIsappoint}




		        and round(6378.138*2*asin(sqrt(pow(sin( (#{postLatitude}*pi()/180-last.poSt_Latitude*pi()/180)/2),2)+cos(#{postLatitude}*pi()/180)*cos(last.poSt_Latitude*pi()/180)* 
	              pow(sin( (#{postLongitude}*pi()/180-last.poSt_Longitude*pi()/180)/2),2)))*1000) 
		        <=#{screenRadius}
