mapper.select:
select pk_release, rele_title, rele_content, rele_createdate, rele_updatedate, rele_usepk, rele_type, rele_img, rele_order, valid_flag from tbl_web_release
1=1
and rele_type=#{releType}
order by rele_img desc,rele_order asc,rele_createdate desc limit #{pager.offset},#{pager.numPerPage}

select count(*) from tbl_web_release
1=1
and rele_type=#{releType}
