mapper.select:
SELECT e.pk_ElectricPile, e.elPi_ElectricPileName, e.elPi_ElectricPileCode, p.poSt_Name, (select maker_Name from tbl_pilemaker where e.elPi_Maker = pk_Carmaker) as elPi_Maker_Name, (SELECT TIMING_CHARGE FROM tbl_ep_timing_charge tc WHERE tc.ELPI_ELECTRICPILECODE = e.elPi_ElectricPileCode) AS TIMING_CHARGE, (SELECT ISSUED_STATUS FROM tbl_ep_timing_charge tc WHERE tc.ELPI_ELECTRICPILECODE = e.elPi_ElectricPileCode) AS ISSUED_STATUS FROM tbl_electricpile e LEFT JOIN tb_m_province a on a.PROVINCE_ID=e.elPi_OwnProvinceCode LEFT JOIN tb_m_city c on c.CITY_ID=e.elPi_OwnCityCode LEFT JOIN tb_m_area d on d.area_id=e.elPi_OwnCountyCode LEFT JOIN tbl_powerstation p on p.pk_PowerStation = e.elPi_RelevancePowerStation where 1=1
and e.elPi_ElectricPileCode LIKE CONCAT('%',#{elpiElectricpilecode},'%' )
and p.poSt_Name LIKE CONCAT('%',#{postName},'%' )
and a.PROVINCE_ID=#{elPiOwnProvinceCode}
and c.CITY_ID=#{elPiOwnCityCode}
and d.area_id=#{elPiOwnCountyCode}
order by e.elPi_Updatedate limit #{pager.offset},#{pager.numPerPage}

SELECT count(*) FROM tbl_electricpile e LEFT JOIN tb_m_province a on a.PROVINCE_ID=e.elPi_OwnProvinceCode LEFT JOIN tb_m_city c on c.CITY_ID=e.elPi_OwnCityCode LEFT JOIN tb_m_area d on d.area_id=e.elPi_OwnCountyCode LEFT JOIN tbl_powerstation p on p.pk_PowerStation = e.elPi_RelevancePowerStation where 1=1
and e.elPi_ElectricPileCode LIKE CONCAT('%',#{elpiElectricpilecode},'%' )
and p.poSt_Name LIKE CONCAT('%',#{postName},'%' )
and a.PROVINCE_ID=#{elPiOwnProvinceCode}
and c.CITY_ID=#{elPiOwnCityCode}
and d.area_id=#{elPiOwnCountyCode}
