SELECT a.pk_ElectricPile, a.elPi_ElectricPileCode, b.pk_ElectricpileHead, b.ePHe_ElectricpileHeadId, a.ep_num, a.comm_status, (
select c.coCo_Content from tbl_configcontent c where c.pk_ConfigContent=a.elPi_PowerSize) elPi_PowerSize, a.elPi_ChargingMode, b.ePHe_ElectricpileHeadState, b.ePHe_ElectricpileHeadName FROM tbl_electricpile a, tbl_electricpilehead b WHERE a.pk_ElectricPile = b.pk_ElectricPile and b.delete_flag = '0'
and a.pk_ElectricPile = #{eId}
and a.elPi_RelevancePowerStation = #{eId}
ORDER BY a.ep_num

SELECT IFNULL(left(min(a.chOr_Createdate),10),'0000-00-00') ch01, IFNULL(count(1),0) chCount01, IFNULL(sum(a.chOr_Moeny),0) chMoney01, IFNULL(sum(a.chOr_QuantityElectricity),0) chQuantity01 FROM tbl_chargingorder a, tbl_electricpile b
WHERE a.chOr_PileNumber = b.elPi_ElectricPileCode
AND a.chOr_ChargingStatus >= 2 AND a.chOr_ChargingStatus!=4 AND b.elPi_ElectricPileCode = #{eCode} AND a.chOr_Muzzle = #{headId}
