mapper.select:
select u.user_id, ifnull(u.norm_name,"---") as usIn_UserName, ifnull(u.norm_real_name,"---") as usIn_FacticityName, u.user_account, (select ifnull(SUM(p.puHi_Monetary),0) as puHi_Monetary from tbl_purchasehistory p where p.puHi_UserId=u.user_id and p.puHi_Type!=4
and p.puHi_PurchaseHistoryTime>=#{startTime}
and p.puHi_PurchaseHistoryTime<=#{endTime}
) AS userTotal_Purchase, (select ifnull(SUM(p.puHi_Monetary),0) as puHi_Monetary from tbl_purchasehistory p where p.puHi_UserId=u.user_id and p.puHi_Type=4
and p.puHi_PurchaseHistoryTime>=#{startTime}
and p.puHi_PurchaseHistoryTime<=#{endTime}
) AS userTotal_recharge , (select norm_account_balance from tbl_user_normal bl where bl.user_id=u.user_id ) as norm_account FROM (select a.user_account,b.user_id,b.norm_real_name,b.norm_name from tbl_user a,tbl_user_normal b where a.user_id=b.user_id ) AS u where 1=1
and u.user_account like CONCAT('%','${usIn_Phone}','%' )
ORDER BY userTotal_Purchase DESC,userTotal_recharge DESC limit #{pager.offset},#{pager.numPerPage}

select ( SELECT ifnull(SUM(co.chOr_ChargeMoney), 0) AS chargeMoney FROM tbl_chargingorder co WHERE p.partnerKey = co.chOr_OrgNo
and p.puHi_PurchaseHistoryTime>=#{startTime}
and p.puHi_PurchaseHistoryTime<=#{endTime}
AND co.chOr_Type =1 ) AS totalChargeMoney, p.partnerName companyName, p.partnerKey companyNameNumber FROM tbl_partner p where 1=1
and p.partnerName like CONCAT('%','${companyName}','%' )
limit #{pager.offset},#{pager.numPerPage}

select count(*) FROM tbl_partner p where 1=1
and p.partnerName like CONCAT('%','${companyName}','%' )
limit #{pager.offset},#{pager.numPerPage}

select
p.pk_PurchaseHistory,
p.puHi_PurchaseHistoryTime,
p.puHi_Monetary,
p.puHi_ChargeType,
p.puHi_Type,
p.puHi_UserId,
t.elPi_ElectricPileName,
t.elPi_ElectricPileAddress,
(SELECT
u.user_account FROM tbl_user u WHERE u.user_id = p.puHi_UserId )
usInPhone
from
tbl_purchasehistory p
left join tbl_electricpile t on p.puHi_ElectricPileCode = t.elPi_ElectricPileCode
where
p.puHi_UserId=#{puHi_UserId}

	and p.puHi_Type=#{puHi_Type}


		and p.puHi_PurchaseHistoryTime>=#{startTime}


		and p.puHi_PurchaseHistoryTime<=#{endTime}


	and t.elPi_OwnProvinceCode = #{proviceCode}


	and t.elPi_OwnCityCode = #{cityCode}


	and t.elPi_OwnCountyCode = #{countyCode}

ORDER BY p.puHi_PurchaseHistoryTime DESC
limit #{pager.offset},#{pager.numPerPage}

		select
			co.pk_ChargingOrder pkChargingOrder,	
			co.chOr_Updatedate chOrTime,
			(co.chOr_ChargeMoney+chOr_ServiceMoney) chOrMoney,
 			1 puHi_Type
		FROM
		tbl_chargingorder co
		where 1=1

				and co.chOr_OrgNo = #{chOrOrgNo}


				and co.chOr_Updatedate>=#{startTime}


				and co.chOr_Updatedate<=#{endTime}

		limit #{pager.offset},#{pager.numPerPage}

select
	count(*)
FROM
tbl_chargingorder co
where 1=1

		and co.chOr_OrgNo = #{chOrOrgNo}


		and co.chOr_Updatedate>=#{startTime}


		and co.chOr_Updatedate<=#{endTime}

limit #{pager.offset},#{pager.numPerPage}

select
count(*)
from
tbl_purchasehistory t
left join tbl_electricpile p on t.puHi_ElectricPileCode = p.elPi_ElectricPileCode
where
puHi_UserId=#{puHi_UserId}

	and puHi_Type=#{puHi_Type}


		and puHi_PurchaseHistoryTime>=#{startTime}


		and puHi_PurchaseHistoryTime<=#{endTime}


	and p.elPi_OwnProvinceCode = #{proviceCode}


	and p.elPi_OwnCityCode = #{cityCode}


	and p.elPi_OwnCountyCode = #{countyCode}

select
ifnull(SUM(puHi_Monetary),0)as userAllPaypuHi_Monetary,
ifnull((select sum(puHi_Monetary) from tbl_purchasehistory
where
puHi_UserId = #{puHi_UserId} and puHi_ChargeType = 1

